function validateForm(e){e.validate({ignore:".ignore",rules:{name:{required:!0,maxlength:150},email:{required:!0,email:150},mobile:{required:!0,number:!0}},messages:{name:{required:"bắt buộc phải nhập title",maxlength:"title có độ dài lớn nhất là {0}"},email:{required:"bắt buộc nhập email",email:"email đã nhập không đúng định dạng"},mobile:{required:"bắt buộc nhập số điện thoại",number:"số điện thoại đã nhập không đúng định dạng"}}})}$(document).ready(function(){var e=$(".js-validate-form");e.length&&validateForm(e)});