function validateForm(e){e.validate({ignore:".ignore",rules:{username:{required:!0,maxlength:40},avatar:{required:!0,maxlength:510}},messages:{username:{required:"bắt buộc phải nhập title",maxlength:"title có độ dài lớn nhất là {0}"},avatar:{required:"bắt buộc nhập title để tạo slug",maxlength:"độ dài slug được tạo ra không vượt quá {0} kí tự"}}})}$(document).ready(function(){var e=$(".js-validate-form");e.length&&validateForm(e)});